import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Line, Bar, Pie } from 'react-chartjs-2';

const App = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(
          'https://api.coingecko.com/api/v3/coins/bitcoin/market_chart?vs_currency=usd&days=7&interval=daily'
        );
        setData(response.data.prices);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();

    const interval = setInterval(fetchData, 30000); // Fetch data every 30 seconds
    return () => clearInterval(interval);
  }, []);

  const formatDate = (timestamp) => {
    const date = new Date(timestamp);
    return date.toLocaleDateString();
  };

  const chartData = {
    labels: data.map((entry) => formatDate(entry[0])),
    datasets: [
      {
        label: 'Bitcoin Price (USD)',
        data: data.map((entry) => entry[1]),
        fill: false,
        borderColor: 'rgb(75, 192, 192)',
        tension: 0.1,
      },
    ],
  };

  const barChartData = {
    labels: data.map((entry) => formatDate(entry[0])),
    datasets: [
      {
        label: 'Bitcoin Price (USD)',
        data: data.map((entry) => entry[1]),
        backgroundColor: 'rgb(75, 192, 192)',
      },
    ],
  };

  const pieChartData = {
    labels: ['Bitcoin', 'Ethereum', 'Litecoin', 'Ripple'],
    datasets: [
      {
        label: 'Cryptocurrency Market Share',
        data: [60, 20, 10, 10], // Example data, replace with actual data
        backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56', '#FF8F1C'],
      },
    ],
  };

  return (
    <div>
      <h1>Cryptocurrency Dashboard</h1>
      <div style={{ display: 'flex', justifyContent: 'space-around', alignItems: 'center' }}>
        <div>
          <h2>Bitcoin Price Trend</h2>
          <Line data={chartData} />
        </div>
        <div>
          <h2>Bitcoin Price Distribution</h2>
          <Bar data={barChartData} />
        </div>
        <div>
          <h2>Cryptocurrency Market Share</h2>
          <Pie data={pieChartData} />
        </div>
      </div>
    </div>
  );
};

export default App;
